import { useSelector, useDispatch } from "react-redux";
import classes from "./Counter.module.css";

const Counter = () => {
  const counter = useSelector(state=>state.count);
  const toggler = useSelector(state=> state.countShown);
  const dispatch = useDispatch();
  const toggleCounterHandler = () => {
    dispatch({type:"toggler"})
  };

  const incrementHandler =()=>{
    dispatch({type:"increment"})
  }

  const increase =()=>{
    dispatch({type:"increase", price:5})
  }

  const decrementtHandler =()=>{
    dispatch({type:"decrement"})
  }


  return (
    <main className={classes.counter}>
      <h1>Redux Counter</h1>
    { toggle && <div className={classes.value}>{counter}</div>}
      <button onClick={incrementHandler}>Increment</button>
      <button onClick={increase}>Increment by 5</button>
      <button onClick={decrementtHandler}>Decrement</button>
      <button onClick={toggleCounterHandler}>Toggle Counter</button>

    </main>
  );
};

export default Counter;
