import { createStore } from "redux";

const countReducer = (state = { count: 0, countShown: true }, action) => {
  if (action.type === "increment") {
    return {
      count: state.count + 1,
      countShown: true,
    };
  }
  if (action.type === "increase") {
    return {
      count: state.count + action.price,
      countShown: true,
    };
  }
  if (action.type === "decrement") {
    return {
      count: state.count - 1,
      countShown: true,
    };
  }

  if (action.type === "toggler") {
    return {
      count: state.count,
      countShown: !state.countShown
    };
  }

  return state;
};

const store = createStore(countReducer);

export default store;
